INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (1, 9, 'Halves the target''s [Defense]{mechanic:defense} for damage calculation, which is similar to doubling the attack''s [power]{mechanic:power}.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (2, 9, 'Hits Pokémon under the effects of []{move:dig} and []{move:fly}.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (3, 9, 'Does nothing in trainer battles.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (4, 9, 'Works while asleep through []{move:sleep-talk} if not at full health.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (5, 9, 'If this move misses, the user takes 1 point of damage in recoil.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (6, 9, 'If this move misses, the user takes half of the damage it would have inflicted in recoil.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (7, 9, 'Locks the user into this move.  Due to a bug, if this move misses, its [accuracy]{mechanic:accuracy} drops to 1/256 as long as the user is still locked in.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (8, 9, 'Can call any move except for []{move:struggle} and itself.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (9, 9, 'Does not interact with []{move:stomp}.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (10, 9, 'Raises [evasion]{mechanic:evasion} by one stage.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (11, 9, 'Does not raise [Defense]{mechanic:defense}.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (12, 9, 'Does not hit Pokémon under the effects of []{move:dig}.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (13, 9, 'Does not hit Pokémon under the effects of []{move:fly}.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (14, 9, 'Does not interact with []{move:minimize}.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (15, 9, 'Does not hit Pokémon under the effects of []{move:fly}.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (16, 9, 'Determines damage using each participant''s base [Attack]{mechanic:attack} and the opponent''s base [Defense]{mechanic:defense} in place of calculated stats.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (17, 9, '[]{move:swift} can hit the user while in the air, and no other moves can.  Due to a bug, if the user fails to carry out the move''s second turn, e.g. due to [paralysis]{mechanic:paralysis}, it remains in the air until it switches out or uses Fly again successfully.  It can act as if it were on the ground in the meantime, but moves still miss it.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (18, 9, 'Does not raise [Defense]{mechanic:defense} or [Special Defense]{mechanic:special-defense}.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (19, 9, 'Does not raise the user''s [Special Defense]{mechanic:special-defense}.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (20, 9, 'Heals replacement Pokémon by half of their max HP.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (21, 9, 'Inflicts []{type:normal}-type damage.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (22, 9, '[]{move:swift} can hit the user while underground, and no other moves can.  Due to a bug, if the user fails to carry out the move''s second turn, e.g. due to [paralysis]{mechanic:paralysis}, it remains underground until it switches out or uses Dig again successfully.  It can act as if it were aboveground in the meantime, but moves still miss it.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (23, 9, 'Does not interact with []{move:hail}.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (24, 9, 'Ignores []{ability:wonder-guard}.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (25, 9, 'Lasts either two or three turns.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (26, 9, 'If the target is not under the effect of []{move:detect} or []{move:protect}, this move will fail.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (27, 9, 'The user receieves 1/2 of the damage dealt in recoil.');
INSERT INTO main.move_effect_changelog_prose (move_effect_changelog_id, local_language_id, effect) VALUES (28, 9, 'The barriers are destroyed even if this move has [no effect]{mechanic:no-effect}.');
