INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (1, 6, 'Kontakt', 'Der Anwender berührt das Ziel.  Dies löst mahcne Fähigkeiten aus (z.B. []{ability:static}) und Gegenstände (z.B. []{item:sticky-barb}).');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (1, 9, 'Makes contact', 'User touches the target.  This triggers some abilities (e.g., []{ability:static}) and items (e.g., []{item:sticky-barb}).');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (2, 6, 'Hat eine Aufladerunde', 'Diese Fähigkeit hat eine Aufladerunde, welche mit einer []{item:power-herb} übersprungen werden kann.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (2, 9, 'Has a charging turn', 'This move has a charging turn that can be skipped with a []{item:power-herb}.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (3, 6, 'Muss aufladen', 'Die Runde nach dieser Fähigkeit wird benötigt, damit das Pokémon sich aufladen kann.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (3, 9, 'Must recharge', 'The turn after this move is used, the Pokémon''s action is skipped so it can recharge.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (4, 6, 'Blockiert von Scanner and Schutzschild', 'Diese Fähigkeit funktioniert nicht, wenn das Ziel []{move:detect} oder []{move:protect} diese Runde genutzt hat.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (4, 9, 'Blocked by Detect and Protect', 'This move will not work if the target has used []{move:detect} or []{move:protect} this turn.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (5, 6, 'Reflektierbar', 'Diese Fähigkeit könnte reflektiert mit []{move:magic-coat} oder []{ability:magic-bounce} reflektiert werden.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (5, 9, 'Reflectable', 'This move may be reflected back at the user with []{move:magic-coat} or []{ability:magic-bounce}.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (6, 6, 'Übernehmbar', 'Diese Fähigkeit kann übernommen werden, wenn ein anderes Pokémon []{move:snatch} diese Runde nutzt.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (6, 9, 'Snatchable', 'This move will be stolen if another Pokémon has used []{move:snatch} this turn.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (7, 6, 'Kopierbar von Spiegeltrick', 'Ein von dieser Fähigkeit getroffenes Pokémon kann []{move:mirror-move} nutzen um diese Fähigkeit zu kopieren.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (7, 9, 'Copied by Mirror Move', 'A Pokémon targeted by this move can use []{move:mirror-move} to copy it.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (8, 6, 'Hieb basierend', 'Diese Fähigkeit hat die 1.2× Kraft, wenn es von einem Pokémon mit []{ability:iron-fist} genutzt wird.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (8, 9, 'Punch-based', 'This move has 1.2× its usual power when used by a Pokémon with []{ability:iron-fist}.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (9, 6, 'Sound basierend', 'Pokémon mit []{ability:soundproof} sind immun gegen diese Fähigkeit.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (9, 9, 'Sound-based', 'Pokémon with []{ability:soundproof} are immune to this move.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (10, 6, 'Nicht nutzbar bei hoher Erdanziehung', 'Diese Fähigkeit kann nicht bei hoher []{move:gravity} genutzt werden.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (10, 9, 'Unusable during Gravity', 'This move cannot be used in high []{move:gravity}.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (11, 6, 'Enteist bei benutzung', 'Diese Fähigkeit kann genutzt werden, wenn das Pokémon vereist ist um es zu enteisen.');
INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (11, 9, 'Defrosts when used', 'This move can be used while frozen to force the Pokémon to defrost.');
