INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (17, 6, 'Megawumme basierend', 'Diese Fähigkeit hat die 1.5× Kraft, wenn sie von einem Pokémon mit []{ability:mega-launcher} genutzt wird.');
