INSERT INTO main.move_flag_prose (move_flag_id, local_language_id, name, description) VALUES (1, 10, 'Způsobí dotek', 'Uživatel se dotkne cíle.  Toto zaktivuje některé schopnosti (např. []{ability:static}) a věci (např. []{item:sticky-barb}).');
