INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (1, 6, 'Level up', 'Wird gelernt, wenn ein Pokémon ein bestimmtes Level erreicht.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (1, 9, 'Level up', 'Learned when a Pokémon reaches a certain level.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (2, 6, 'Ei', 'Erscheint bei einem frisch geschlüpften Pokémon, wenn der Vater die selbe Fähigkeit hatte.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (2, 9, 'Egg', 'Appears on a newly-hatched Pokémon, if the father had the same move.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (3, 6, 'Tutor', 'Kann jederzeit von einem NPC erlernt werden.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (3, 9, 'Tutor', 'Can be taught at any time by an NPC.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (4, 6, 'Maschine', 'Kann jederzeit durch das benutzen eines TM oder VM erlernt werden.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (4, 9, 'Machine', 'Can be taught at any time by using a TM or HM.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (5, 6, 'Stadium: Surfendes Pikachu', 'Wird gelernt, wenn ein Pikachu dabei hilft den Premium-Cup in der Kategorie Meisterball ohne unterbrechung zu besiegen.  Es muss in jedem Kampf teilnehmen.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (5, 9, 'Stadium: Surfing Pikachu', 'Learned when a non-rental Pikachu helps beat Prime Cup Master Ball R-2.  It must participate in every battle, and you must win with no continues.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (6, 6, 'Volttackle Pichu', 'Erscheint bei einem Pichu dessen Mutter einen Kugelblitz trug.  Der Vater darf nicht Ditto sein.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (6, 9, 'Volt Tackle Pichu', 'Appears on a Pichu whose mother was holding a Light Ball.  The father cannot be Ditto.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (7, 9, 'Colosseum: Purification', 'Appears on a Shadow Pokémon as it becomes increasingly purified.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (8, 9, 'XD: Shadow', 'Appears on a Snatched Shadow Pokémon.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (9, 9, 'XD: Purification', 'Appears on a Shadow Pokémon as it becomes increasingly purified.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (10, 6, 'Rotom Formen', 'Erscheint, wenn Rotom seine Form wechselt.  Verschwindet wenn Rotom eine andere Form an nimmt. Diese Fähigkeit kann nur durch wechseln der Form erlernt werden.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (10, 9, 'Form Change', 'Appears when Rotom or Cosplay Pikachu changes form.  Disappears if the Pokémon becomes another form and this move can only be learned by form change.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (4, 10, 'Přístrojem', 'Učené použitím TM nebo HM');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (5, 10, 'Stadium: Surfující Pikachu', NULL);
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (10, 10, 'Forma Rotoma', 'Objeví se u Rotoma, když se změní do určité formy.  Zmizí, když se Rotom změní do jiné formy, pokud se tento útok dá naučit jen změnou formy.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (3, 10, 'Od učitele', 'Učené nějakou postavou ve hře.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (6, 10, 'Light Ball', 'Objeví se u Pichu, jehož matka držela Light Ball. Otec nesmí být Ditto.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (8, 10, 'XD: Stínový útok', 'U ukradených „Shadow“ pokémonů');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (2, 10, 'Vajíčkový', 'Dědí se od otce.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (9, 10, 'XD: Purifikace', 'Objeví se u „Shadow“ pokémona, jak se mu otevírá srdce.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (7, 10, 'Colosseum: Purifikace', 'Objeví se u „Shadow“ pokémona, jak se mu otevírá srdce.');
INSERT INTO main.pokemon_move_method_prose (pokemon_move_method_id, local_language_id, name, description) VALUES (1, 10, 'Úrovní', 'Učení dosažením určité úrovně.');
